import React from 'react'
import Button from './button'


const styles = {
  container: {
    alignItems: 'center',
    display: 'flex',
    height: '100vh',
  } 
};

class Keypad extends React.Component {
    constructor(props) {
      super(props);
      this.state = {};
    }
  
    render() {
      return (
        <div>
          {this.props.buttons.map(btnItem => {
            return (
              <Button
                btn={btnItem}
                handleClick={this.props.handleButtonClickParent}
              />
            );
          })}
        </div>
      );
    }
  }

  export default withStyles(styles)(Keypad);
